mapper.update:
update emp

    username=#{username}, password=#{password}, real_name=#{realName},
    role = #{role}, state=#{state}, img = #{img}

where id = #{id}

mapper.select:
select id, username, password, real_name realName, role, state, img, create_time createTime from emp
username = #{username} and
real_name = #{realName} and
role = #{role} and
state = #{state}
;
